import * as React from 'react';
import { styled, useTheme } from '@mui/material/styles';
import { Link } from 'react-router-dom';
import {
  Typography,
  Box,
  Drawer,
  Toolbar,
  CssBaseline,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  IconButton,
} from '@mui/material';
import MuiAppBar from '@mui/material/AppBar';
import {
  MenuIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
  TvIcon,
  ManageSearchIcon,
} from '@mui/icons-material';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import TheatersIcon from '@mui/icons-material/Theaters';
const drawerWidth = 240;

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginRight: drawerWidth,
  }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-start',
}));

export default function PersistentDrawerRight() {
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const NavItems = [
    { id: '1', text: 'Trending', path: '/trending', icon: <TrendingUpIcon /> },
    { id: '2', text: 'Movies', path: '/movies', icon: <TheatersIcon /> },
    { id: '3', text: 'Series', path: '/series', icon: <TvIcon /> },
    { id: '4', text: 'Search', path: '/search', icon: <ManageSearchIcon /> },
  ];

  return (
    <>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <AppBar position='fixed' open={open}>
          <Toolbar>
            <Typography
              variant='h6'
              gutterBottom
              noWrap
              sx={{ flexGrow: 1 }}
              component='div'>
              CloudFlix <br />
              <Typography gutterBottom variant='body2'>
                Your Movie Search App
              </Typography>
            </Typography>

            <IconButton
              color='inherit'
              aria-label='open drawer'
              edge='end'
              onClick={handleDrawerOpen}
              sx={{ ...(open && { display: 'none' }) }}>
              <MenuIcon />
            </IconButton>
          </Toolbar>
        </AppBar>

        <Drawer
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: drawerWidth,
            },
          }}
          variant='persistent'
          anchor='right'
          open={open}>
          <DrawerHeader>
            <IconButton onClick={handleDrawerClose}>
              {theme.direction === 'rtl' ? (
                <ChevronLeftIcon />
              ) : (
                <ChevronRightIcon />
              )}
            </IconButton>
          </DrawerHeader>
          <Divider />

          <List>
            {NavItems.map((item, key) => {
              // const { id, path, text, icon } = item;
              return (
                <Link to={item.path}>
                  <ListItem button key={item.id}>
                    <ListItemIcon>{item.icon}</ListItemIcon>
                    <ListItemText primary={item} />
                  </ListItem>
                </Link>
              );
            })}
          </List>
        </Drawer>
      </Box>
      <AppBar position='fixed' color='primary' sx={{ top: 'auto', bottom: 0 }}>
        <Toolbar></Toolbar>
        <Toolbar>
          <List>
            {NavItems.map((item, key) => {
              // const { id, path, text, icon } = item;
              return (
                <Link to={item.path}>
                  <ListItem button key={item.id}>
                    <ListItemIcon>{item.icon}</ListItemIcon>
                    <ListItemText primary={item} />
                  </ListItem>
                </Link>
              );
            })}
          </List>
        </Toolbar>
      </AppBar>
    </>
  );
}
