import axios from 'axios';

import { useEffect, useState } from 'react';
import SingleContent from '../SingleContent';
// import CustomPagination from '../../components/Pagination/CustomPagination';

const Trends = () => {
  // const [page, setPage] = useState(1);
  const [content, setContent] = useState([]);

  const fetchTrending = async () => {
    const { data } = await axios.get(
      // 'https://api.themoviedb.org/3/trending/all/day?api_key=3f9c5d7d2e230b943db664ab61427bde'

      `https://api.themoviedb.org/3/trending/all/day?api_key=${process.env.REACT_APP_MOVIE_KEY}`
    );

    if ('REACT_APP_MOVIE_KEY' in Object.keys(process.env))
      console.log('It is set!');
    else console.log('Not set!');
    // &page=${page}
    console.log(data);
    setContent(data.results);
  };

  useEffect(() => {
    // window.scroll(0, 0);
    fetchTrending();
    // eslint-disable-next-linepage
  }, []);

  return (
    <>
      <div>
        <span className='pageTitle'>Trending Today</span>

        <div className='trending'>
          {content && content.map((c) => <SingleContent />)}
        </div>
      </div>
    </>
  );
};
